import "./Expenses.css";
import React, { useState } from 'react';
import ExpenseItem from "./ExpenseItem";
import Card from "../components/UI/Card";
import ExpensesFilter from "./ExpenseFilter";

function Expenses(props) {

  let expenseContents = <p>The Year has no Entries</p>

  if (filteredExpense > 0) {
    expenseContents = filteredExpense.map(expe => <ExpenseItem
      key={expe.id}
      title={expe.title}
      amount={expe.amount}
      date={expe.date} />)
  }

  const [enteredYear, setEnteredYear] = useState('2020');

  const filterChangeHandler = selectedYear => {
    setEnteredYear(selectedYear);

  }
  console.log();
  const filteredExpense = props.items.filter(expense => {
    return String(expense.date.getFullYear()) === enteredYear;
  })

  return (
    <div>
      <Card className="expenses">

        <ExpensesFilter onEnteredYear={enteredYear}
          onSelectedYear={filterChangeHandler} />

        {expenseContents}



      </Card>
    </div>
  );
}
export default Expenses;
